package Tools;import Main.Eco;import Main.Umano;import javafx.concurrent.Task;/**  Classe MyTask:**  Questa classe implementa il metodo call che esegue un thread di javafx*  per l'aggiornamento di interfacce grafiche** */public class MyTask extends Task<Void> {    @Override    protected Void call() throws Exception {		boolean flag = true;        while (flag) {            updateMessage("AVV: "+ Eco.getAvv());        }        return null;    }}